namespace ClassLibrary1
{
    public partial class Manager
    {
        public ReadOnlyCollection<Valorant> Valorants { get; set; }
        public ReadOnlyCollection<Carte> Cartes { get; set; }

        List<Carte> carte = new List<Carte>();
