            Valorants = new ReadOnlyCollection<Valorant>(valorant);
            Cartes = new ReadOnlyCollection<Carte>(carte);
        }
        public void ChargeValorant()
        {
            valorant.AddRange(new Valorant[]
            {
                new Valorant("Phoenix","Dueslist",new ListeAg(), "United Kingdom",new Ultime(6, "Phoenix place une marque au sol. Si Phoenix meurt ou que la durée de la capacité expire, Phoenix réapparaît sur la marque avec tous ses points de vie.", "Revanche", "/PhoX.png"),new Signature(0,1,3.25, "Phoenix s’équipe d’une boule de feu. TIREZ pour lancer la boule de feu qui explose en touchant le sol, créant une zone enflammée qui blesse les unités qui se tiennent dessus.",2,"Kills", "Mains Brûlantes", "/PhoE.png"),new Capacite(250,2,0.7, "Phoenix s’équipe d’un orbe qui explose peut après avoir été lancé, aveuglant les unités qui le regardaient. TIREZ pour que le tir de l’orbe décrive une courbe vers la gauche. TIR ALTERNATIF pour que l’orbe décrive une courbe vers la droite.", "Balle Courbe", "/PhoA.png"),new Capacite(200,1,8, "Phoenix s’équipe d’un mur de feu. TIREZ pour créer une ligne de flamme créant un mur de feu bloquant la vision des joueurs et blessant les adversaires et alliés qui passent à travers. MAINTENEZ votre tir pour légèrement dévier la trajectoire du mur vers votre viseur.", "Brasier", "/PhoC.png"),new Passif("null","null"), "/VideoSpell/Phoenix/pho.jpg", "/VideoSpell/Phoenix/RU.jpg", "En provenance du Royaume-Uni, Phoenix illumine le champ de bataille avec ses pouvoirs astraux et son style de combat flamboyant. Peu importe que les renforts arrivent ou non, il fonce au combat quand il le décide."),
                new Valorant("Jett", "Duelist", new ListeAg(), "Coréenne", new Ultime(7, "Jett s’équipe de 7 couteaux de lancer. TIREZ pour lancer un couteau sur une trajectoire droite et précise. Jett récupère tous ses couteaux chaque fois qu’elle tue un adversaire avec l’un d’eux.", "Tempête de Lames","/VideoSpell/Jett/JX.png"), new Signature(0, 1, 0.0, "Jett se propulse dans la direction souhaitée", 2, "Eliminations", "Vent arrière","/VideoSpell/Jett/JE.png"), new Capacite(150, 2, 0.6, "Jett se propulse instantanément dans les airs.", "Courant ascendant","/VideoSpell/Jett/JA.png"), new Capacite(200, 2, 4.5,"Lance une fumigène qui se déclanche au contact d'une surface", "Cloud Burst","/VideoSpell/Jett/JC.png"), new Passif("Drift est une abilité qui permet à Jett de planer lorsque qu'elle saute d'une grande hauteur.", "Drift"),"VideoSpell/Jett/jett.jpg","/VideoSpell/Jett/coree.jpg","Représentante de sa patrie, la Corée du Sud, Jett dispose d'un style de combat basé sur l'agilité et l'esquive, qui lui permet de prendre des risques qu'elle seule peut se permettre de prendre. Elle tourne autour des affrontements et découpe ses ennemis avant même qu'ils ne s'en rendent compte."),
                new Valorant("Yoru","Duelist",new ListeAg(),"Japan",new Ultime(7, "EQUIPEZ-vous d’un masque qui permet de voir entre les dimensions.", "Transfert Dimensionnel", "/VideoSpell/Yoru/YX.png"),new Signature(200,2,30, "EQUIPEZ-vous d’une balise dimensionnelle.TIREZ pour envoyer la balise vers l’avant.UTILISEZ le tir secondaire pour poser la balise devant vous.Activez la balise pour vous y téléporter.UTILISEZ la balise pour feindre la téléportation.",2,"Kill", "Visite Surprise", "/VideoSpell/Yoru/YE.png"),new Capacite(250,2,0.6, "EQUIPEZ-vous d’un fragment dimensionnel instable arraché à la réalité.TIREZ pour lancer le fragment, qui provoquera un éclat aveuglant peu après avoir touché une surface dure.", "Angle Mort", "/VideoSpell/Yoru/YA.png"),new Capacite(100,1,10, "TIREZ pour activer instantanément le double et l’envoyer vers l’avant. Utilisez le TIR SECONDAIRE pour poser un écho inactif devant vous sans l’activer.", "Feinte ", "/VideoSpell/Yoru/YC.png"),new Passif("Null","Null"), "/VideoSpell/Yoru/yoru.png", "/VideoSpell/Yoru/japon.jpg", "Yoru est un Agent qui aime tromper les sens de ses adversaires. Impossible de se fier à ses yeux ou à ses oreilles quand il prend part au combat. Yoru est un Duelliste japonais capable de se déplacer sans être vu ou de faire croire qu’il se déplace. Il s’agit d’un Agent très technique à utiliser."),
                new Valorant("Neon","Duelist",new ListeAg(), "Philippines",new Ultime(7, "Libérez toute la puissance et la vitesse de Néon pendant un court instant. TIREZ pour canaliser son pouvoir en un rayon électrique mortel dont la précision reste élevée même en mouvement. La durée et la glissade se réinitialisent à chaque élimination.", "Ultra Vitesse","/Neon_X-Overdrive.png"),new Signature(0,1,10, "Chargez INSTANTANÉMENT le pouvoir de Néon pour décupler sa vitesse. À la fin du chargement, utilisez le TIR SECONDAIRE pour faire une glissade électrique. Vous regagnez une charge de glissade toutes les deux éliminations.",2,"Kills", "Vitesse supérieure", "/Neon_E-High_Gear.png"),new Capacite(200,2,1.1, "Lancez INSTANTANÉMENT un éclair d’énergie qui rebondit une fois. Chaque fois qu’il rencontre une surface, l’éclair électrifie le sol en dessous avec une explosion désorientante.", "Éclair relais", "/Neon_Q-Relay_Bolt.png"),new Capacite(300,1,6, "TIREZ deux lignes d’énergie dans le sol devant vous. Après avoir parcouru une courte distance ou touché une surface, elles créent des murs d’électricité statique qui bloquent la vision et blessent les ennemis qui les traversent.", "Voie Rapide", "/Neon_C-Fast_Lane.png"),new Passif("Utiliser les capacités de neon consomme de l'énergie. Tuer un agent ennemi recharge instantanément l'énergie.","Energie"), "VideoSpell/Neon/neon.jpg", "/VideoSpell/Neon/philipine.jpg", " L'agent philippin, Neon, s'élance vers l'avant à une vitesse fulgurante, libérant de grosses décharges de radiance biomagnétique générées frénétiquement par son corps. Elle se lance à la poursuite des ennemis qui n'ont pas le temps de s'y préparer et les élimine aussi vite que l'éclair."),
                new Valorant("Raze","Duelist",new ListeAg(),"Brazil",new Ultime(8, "Raze s’équipe d’un lance-roquette. TIREZ pour lancer une roquette qui explose dès qu’elle rentre en contact avec un joueur ou un obstacle, créant une grande déflagration qui blesse les joueurs alentours.", "Bouquet final", "/RX.png"),new Signature(0,1,3, "Raze s’équipe d’une grenade à fragmentation. TIREZ pour jeter la grenade qui explose en faisant des dégâts autour d’elle, avant de se diviser en plus petites grenades qui explosent à leur tour autour de l’explosion initiale.",2,"Kills", "Grenade Gigogne","/RE.png"),new Capacite(200,2,5, "Raze jette instantanément un dispositif qui s’accroche à la première surface touchée. RÉUTILISEZ la capacité après qu’elle ait été déployée pour la faire exploser, blessant et propulsant tous les joueurs à portée. Raze et ses alliés ne subissent pas de dégâts du Pack Explosif et peuvent l’utiliser afin de se propulser dans les airs.", "Pack explosif", "/RA.png"),new Capacite(300,1,10, "Raze s’équipe d’un robot explosif. TIREZ pour placer au sol un robot qui avance en ligne droite et rebondit contre les murs. Il explose et occasionne de gros dégâts s’il détecte un adversaire devant lui.", "Boum Bot", "/RC.png"),new Passif("Null", "Null"), "/VideoSpell/Raze/raze.jpg", "/VideoSpell/Raze/bresil.jpg", "Armée de sa personnalité et de sa grosse artillerie, Raze fait une entrée explosive depuis le Brésil. Grâce à sa force brute, elle excelle à débusquer les ennemis retranchés et à faire le ménage dans les espaces étroits, le tout avec une bonne dose de « boum »"),
                new Valorant("Reyna","Duelist",new ListeAg(), "Mexico",new Ultime(6, "Entrez INSTANTANÉMENT en furie, ce qui augmente considérablement vos vitesses de tir, de chargement d’arme et de rechargement. Vous gagnez une infinité de charges pour les compétences se servant de Récolte des âmes. Réussir une élimination réinitialise la durée.", "Impératrice", "/TX_Reyna_X.png"),new Signature(200,2,2, "Vous consommez INSTANTANÉMENT un orbe d’âme proche et devenez intangible pendant un court instant. Si IMPÉRATRICE est active, vous devenez également invisible.",0,"Null", "Rebuffade", "/TX_Reyna_E.png"),new Capacite(200,2,3, "Récolte des âmes : les ennemis tués par Reyna laissent des orbes d’âme qui durent 3  sec. Dévoration : vous consommez INSTANTANÉMENT un orbe d’âme proche pour rapidement regagner des PV pendant un court instant. Au-delà de 100 PV, les PV ainsi gagnés sont perdus progressivement. Si IMPÉRATRICE est active, cette compétence se lancera automatiquement et ne consommera pas d’orbe d’âme.", "Dévoration", "/TX_Reyna_Q.png"),new Capacite(250,2,2, "Compétence Signature – 2 Utilisation possible par tour.ÉQUIPEZ - vous d’un œil éthéré destrucible.ACTIVEZ pour lancer l’oeil vers l’avant sur une courte distance.L’œil rend myope tous les ennemis qui le regardent.", "Œillade", "/TX_Reyna_C.png"),new Passif("Récolte d'âme", "Les ennemis qui meurent de Reyna, ou meurent dans les 3 secondes après avoir subi des dégâts de Reyna, laissent derrière eux des orbes d'âme qui durent 3 secondes."), "VideoSpell/Reyna/reyna.jpg", "/VideoSpell/Reyna/mexique.jpg", " Originaire du cœur du Mexique, Reyna est une experte des combats singuliers qui se renforce à chaque élimination qu'elle réussit. Son efficacité n'est limitée que par son habileté, ce qui la rend très dépendante de ses propres performances."),
                new Valorant("Astra", "Controller", new ListeAg(), "Ghana", new Ultime(7, "Lorsque Division cosmique est chargée, utilisez le TIR SECONDAIRE en forme astrale pour viser, puis le TIR PRINCIPAL pour sélectionner deux emplaements. Une division cosmique infinie connectera les deux points sélectionnés. La division cosmique bloque les balles et étouffe fortement les sons.","Division Cosmique", "/VideoSpell/Astra/AX.png"), new Signature(150, 4, 25, "ACTIVEZ pour entrer en forme astrale, ce qui vous permet de placer des étoiles avec le TIR PRINCIPAL. Vous pouvez ensuite réactiver ces étoiles pour les transformer en impulsion nova, nébuleuse ou puits de gravité.", 0, "Null", "Forme Astrale", "/VideoSpell/Astra/AX.png"), new Capacite(0, 1, 1.25, "ACTIVEZ une étoile pour faire exploser une impulsion nova. L’impulsion nova se charge brièvement avant d’exploser, désorientant tous les joueurs dans son rayon d’effet.", "Impulsion Nova", "/VideoSpell/Astra/AA.png"), new Capacite(0, 2, 14.25, "ACTIVEZ une étoile pour la transformer en nébuleuse (fumigène).UTILISEZ(F) une étoile pour la dissiper, ce qui vous permettra d’en placer une autre à un autre endroit après un délai.La dissipation forme brièvement une fausse nébuleuse à l’emplacement de l’étoile.", "Nébuleuse", "/VideoSpell/Astra/AE.png"), new Passif("null", "Null"), "VideoSpell/Astra/astra.jpg", "/VideoSpell/Astra/ghana.png", "L'agent ghanéen Astra canalise les énergies du cosmos pour façonner le champ de bataille à sa convenance. Avec une maîtrise complète de sa forme astrale et un talent pour la planification stratégique, elle a toujours une large avance sur ses ennemis."),
                new Valorant("Brimstone","Controller",new ListeAg(),"United States",new Ultime(7,"Brimstone affiche la carte aux alentours. TIREZ pour lancer une frappe orbitale à l’endroit ciblé sur la carte, infligeant énormément de dégâts aux ennemis dans la zone. Avant de tomber, la zone ciblée par Brimstone est visible par les ennemis, ce qui leur permet de s’échapper.","Rayon Orbital","/VideoSpell/Brim/BX.png"),new Signature(100,2,19.25,"Brimstone affiche la carte aux alentours. TIREZ pour marquer jusqu’à trois endroits sur la carte. TIR ALTERNATIF pour faire tomber du ciel des grenades fumigènes qui créent un nuage impactant la vision.",0,"Null","Frappe Fumigène","/VideoSpell/Brim/BE.png"),new Capacite(250,1,7,"Brimstone s’équipe d’un lance-grenade incendiaire. TIREZ pour lancer une grenade qui explose dès qu’elle touche le sol, créant une large zone enflammée blessant tous les joueurs qui se tiennent dessus.","Bombe Incendiaire","/VideoSpell/Brim/BA.png"),new Capacite(200,1,12,"Brimstone jette INSTANTANÉMENT une balise au sol, créant une large zone augmentant la cadence de tir et la vitesse de déplacement de tous les joueurs se tenant à l’intérieur.","Balise Stimulante","/VideoSpeLl/Brim/BC.png"),new Passif("Null","Null"),"/VideoSpell/Brim/Brim.jpeg","/USA.jpg","Tout droit venu des États-Unis d'Amérique, Brimstone possède un arsenal orbital qui permet à son escouade de toujours avoir l'avantage. La précision et la portée de ses compétences utilitaires font de lui un commandant sans égal sur le terrain."),
                new Valorant("Omen","Controller",new ListeAg(),"Earth",new Ultime(7,"Omen ouvre la carte devant lui et peut sélectionner un point de téléportation. TIREZ pour vous téléporter sur le point ciblé après une courte canalisation. Les adversaires peuvent empêcher votre téléportation en détruisant votre ombre avant la fin de la canalisation. APPUYEZ sur EQUIPER pour annuler l’utilisation de l’ultime.","Depuis les Ombres","/VideoSpell/Omen/OX.png"),new Signature(150,2,15,"Omen entre dans un monde parallèle et s’équipe d’un orbe d’ombre à placer sur la carte. La portée peut être ajustée via un indicateur. TIREZ pour faire apparaître un nuage de fumée à l’endroit où se trouve l’indicateur, qui bloque la vision de vos adversaires. MAINTENEZ le tir secondaire pour éloigner le marqueur et MAINTENEZ la touche du skill pour le rapprocher. APPUYEZ sur RECHARGER pour repasser en vue normale.",30,"Secondes","Voile Sombre","/VideoSpell/Omen/OE.png"),new Capacite(300,1,2,"Omen lance instantanément un projectile d’ombre qui passe à travers les murs sur une ligne droite. Tous les joueurs touchés par cette capacité voient leur distance de vision réduite à un mètre.","Paranoïa","/VideoSpell/Omen/OA.png"),new Capacite(100,2,100,"Omen se prépare à se téléporter. TIREZ pour qu’Omen se téléporte sous votre viseur après une brève canalisation. La téléportation ne laisse aucune trace sur votre passage. En revanche, vous faites un bruit caractéristique lorsque vous apparaissez, ce qui peut alerter les ennemis.","Voie des Ombres","/VideoSpell/Omen/OA.png"),new Passif("Null","Null"),"VideoSpell/Omen/Omen.jpg","Null","Redoutable contrôleur, Omen se complaît dans les ténèbres. Il manipule les ombres et peut ainsi grandement réduire la vision de ses ennemis ou encore s’enfuir en se téléportant. Sa capacité à se déplacer sur la carte n’a pas d’égal dans Valorant. Autant dire que cela sème rapidement le trouble et la peur chez ses adversaires."),
                new Valorant("Viper","Controller",new ListeAg(),"United States",new Ultime(7,"Viper s’équipe d’un diffuseur chimique. TIREZ pour créer un large nuage de poison tout autour de Viper, réduisant la visibilité et blessant tous les adversaires se tenant à l’intérieur. Le nuage ne se dissipe pas tant que Viper reste à l’intérieur.Les unités récupèrent petit à petit leurs points de vie quand elles sortent du poison.","Nid de Vipères","/VideoSpell/Viper/VX.png"),new Signature(0,1,8,"Viper s’équipe d’un lance-diffuseur de gaz qui traverse les obstacles. TIREZ pour déployer des émetteurs sur une longue ligne face à Viper. RELANCEZ la capacité pour que l’émetteur crée un mur de poison, impactant la visibilité et blessant les adversaires le traversant. Les unités récupèrent petit à petit leurs points de vie quand elles sortent du poison.",0,"Null","Écran Toxique","/VideoSpell/Viper/VE.png"),new Capacite(200,1,8,"Viper s’équipe d’un émetteur de gaz. TIREZ pour jeter l’émetteur. RELANCEZ la capacité pour que l’émetteur crée un grand nuage de poison, impactant la visibilité et blessant les adversaires se tenant à l’intérieur. L’émetteur reste en place pour l’intégralité du round, mais peut être ramassé pour être utilisé à nouveau. Les unités récupèrent petit à petit leurs points de vie quand elles sortent du poison.","Nuage de Poison","/VideoSpell/Viper/VA.png"),new Capacite(200,2,5.5,"Viper s’équipe d’un lance-grenade chimique. Viper lance une capsule de poison qui se brise lorsqu’elle touche le sol, créant une flaque qui blesse tous les adversaires se tenant dessus et leur inflige l’altération d’état Fragile. Les unités récupèrent petit à petit leurs points de vie quand elles sortent du poison.","Morsure de Serpent","/VideoSpeLl/Viper/VC.png"),new Passif("Les capacités qui appliquent la toxine font que les ennemis affectés voient instantanément leur santé maximale décroître, ce qui diminue plus ils restent longtemps dans la zone d'effet de la capacité.","Toxine"),"VideoSpell/Viper/Viper.jpeg","/USA.jpg","L’Agent Viper fait partie des contrôleurs de Valorant. Grâce à ses capacités, elle peut affecter l’environnement en créant des zones dangereuses qui handicapent l’équipe ennemie, en affectant leur mouvement ou leur visibilité. Poisons et toxines sont donc ses armes de prédilection, et elle sera une alliée de choix pour contrôler le terrain et amener son équipe vers la victoire."),
                new Valorant("Breach","initiator", new ListeAg(),"Sweden",new Ultime(7,"Breach s’équipe d’une charge sismique. TIREZ pour créer une onde de choc dans un large cône face à Breach, ralentissant et blessant les adversaires sur son passage. Cette capacité passe à travers les murs.","Onde Sismique","/VideoSpell/Breach/BX.png"),new Signature(0,1,40,"Breach s’équipe d’une charge sismique. MAINTENEZ votre tir pour augmenter la distance du tir. RELÂCHEZ pour créer une onde sismique, réduisant le champ de vision et la vitesse des adversaires touchés dans une large zone face à Breach. Cette capacité passe à travers les murs et obstacles.",40,"Secondes","Ligne de Fracture","/VideoSpell/Breach/BE.png"),new Capacite(250,2,2,"Breach s’équipe d’une charge aveuglante. TIREZ pour lancer un projectile qui traverse un mur de part en part et aveugle les ennemis à la sortie.","Point d’Ignition","/VideoSpell/Breach/BA.png"),new Capacite(200,1,2.2,"Breach s’équipe d’une charge de fusion. TIREZ pour lancer un projectile qui traverse un mur de part en part et explose à la sortie.","Réplique","/VideoSpell/Breach/BE.png"),new Passif("Null","Null"),"/VideoSpell/Breach/breach.jpg","/VideoSpell/Breach/suede.png","Initateur qui en impose, Breach est capable de faire de lourds dégâts. Mais ce n’est pas là son seul atout : il peut grandement limiter la vision des ennemis via deux de ses compétences. Il ne fait pas dans la finesse, et son kit est très complémentaire. Vous l’aurez compris, cela fait de Breach un Agent de choix sur Valorant."),
                new Valorant("Fade", "Initiator",new ListeAg(),"Turkey",new Ultime(7,"ÉQUIPEZ-vous du pouvoir de la peur. TIREZ pour envoyer une vague d'énergie cauchemardesque qui peut traverser les murs. L'énergie crée une piste vers l'ennemi, en plus de l'assourdir et de désagréger ses PV.","NUIT TOMBANTE","/VideoSpell/Fade/FX.png"),new Signature(0,1,12,"Équipez-vous d'une entité cauchemardesque. TIREZ pour lancer l'orbe. Celui-ci tombe au sol après un temps défini, et se transforme alors en entité cauchemardesque qui révèle les ennemis dans son champ de vision. Les ennemis peuvent détruire cette entité. RÉUTILISEZ la compétence pour faire tomber le projectile plus tôt.",40,"Secondes","HANTEUR","/VideoSpell/Fade/FE.png"),new Capacite(200,2,4.5,"Équipez-vous d'un orbe d'encre cauchemardesque. TIREZ pour lancer l'orbe. Celui-ci tombe au sol après un temps défini, ce qui fait exploser l'encre et crée une zone. Les ennemis pris dans cette zone ne peuvent pas en sortir par des moyens normaux. RÉUTILISEZ la compétence pour faire tomber le projectile plus tôt.","CAPTURE","/VideoSpell/Fade/FA.png"),new Capacite(250,2,3,"ÉQUIPEZ-vous d'un Rôdeur. TIREZ pour envoyer le Rôdeur sur une ligne droite. Le Rôdeur se verrouille sur les ennemis ou les pistes qui entrent dans son cône de vision frontal et les suit ; s'il atteint un ennemi, il le rend myope. MAINTENEZ la touche de TIR pour guider le Rôdeur dans la direction de votre réticule","Rodeur","/VideoSpeLl/Fade/FC.png"),new Passif("null","Null"),"/VideoSpell/Fade/fade.jpg","/VideoSpell/Fade/turquie.webp","Originaire de Turquie, la chasseuse de primes Fade utilise le pouvoir des cauchemars pour s\'emparer des secrets ennemis. Elle traque ses cibles et révèle leurs plus grandes peurs pour mieux les briser dans l\'obscurité."),
                new Valorant("KAY/O","initiator",new ListeAg(), "Alternate Timeline Earth",new Ultime(7, "Entrez INSTANTANÉMENT en surcharge, ce qui permet à KAY/O d’émettre des vagues d’énergie radianite polarisée dans un très large rayon. Les ennemis touchés par ces vagues sont neutralisés pendant un court instant.Pendant la surcharge, KAY / O gagne l’effet Stimulant de combat et peut être restabilisé s’il est mis à terre.", "CMD/null","/VideoSpell/Kayo/KX.png"),new Signature(0,1,1,"ÉQUIPEZ-vous d’une lame neutralisante.TIREZ pour la lancer. La lame se colle à la première surface touchée. Après un délai, elle neutralise tous ceux dans le rayon de l’explosion.",40,"Secondes","POINT/zéro","/VideoSpell/Kayo/KE.png"),new Capacite(250,2,1.6,"ÉQUIPEZ-vous d’une grenade aveuglante.TIREZ pour la lancer. La grenade explose après un court instant et aveugle tous ceux qui voient l’explosion.","MÉMOIRE/Flash","/VideoSpell/Kayo/KA.png"),new Capacite(200,1,2,"ÉQUIPEZ-vous d’un fragment explosif. TIREZ pour le lancer. Le fragment se colle au sol et explose à plusieurs reprises, infligeant des dégâts quasi mortels au centre de chaque explosion.","FRAG/ment","/VideoSpeLl/Kayo/KC.png"),new Passif("Null","Null"),"/VideoSpell/Kayo/kayo.jpg","Null","KAY/O est un Agent qui fera le bonheur des fans de FPS traditionnels. Cet Initiateur dispose de deux grenades très classiques, mais très efficace. KAY/O est également le premier Agent capable de réduire au silence ses adversaires."),
                new Valorant("Skye","Initiator",new ListeAg(),"Australia",new Ultime(7,"ÉQUIPEZ-vous d’une breloque Traqueurs.TIREZ pour lancer trois traqueurs qui rechercheront les trois ennemis les plus proches. Si un traqueur atteint sa cible, il réduit son champ de vision.","Seekers","/VideoSpell/Sky/SX.png"),new Signature(250,2,4,"TIREZ pour lancer l’oiseau devant vous. MAINTENEZ LE TIR pour guider le faucon dans la direction de votre réticule.RÉUTILISEZ la compétence pendant que le faucon est dans les airs pour le transformer en lumière qui envoie une confirmation de tir si un ennemi était à portée et en ligne de mire.",0,"Null","Guiding Light","/VideoSpell/Sky/SE.png"),new Capacite(250,1,3,"TIREZ pour lancer et prendre le contrôle du prédateur.Lorsque vous le contrôlez, TIREZ pour bondir en avant, ce qui provoque une explosion qui désoriente et blesse les ennemis directement touchés.","Trailblazer","/VideoSpell/Sky/SA.png"),new Capacite(200,1,5,"ÉQUIPEZ-vous d’une breloque de soin. Peut être réutilisé jusqu’à épuisement de la capacité de soin.MAINTENEZ LE TIR pour effectuer une canalisation qui soigne les alliés à portée et dans la ligne de mire.Skye ne peut pas se soigner.","Regrowth","/VideoSpeLl/Sky/SC.png"),new Passif("Null","Null"),"/VideoSpell/Sky/Sky1.jpg","/VideoSpell/Sky/sky.jpg","Skye est une Agent spécialisée dans la chasse. D’origine australienne, elle possède une affinité particulière avec les esprits des animaux. Skye est une Sentinelle qui utilisent ses compétences pour traquer et incapacité ses proies. Elle punit durement ses adversaires inattentifs ou qui manquent de réactivité."),
                new Valorant("Sova","Initiator",new ListeAg(),"Russia",new Ultime(6,"Sova s’équipe d’un arc et de trois flèches. TIREZ pour lancer une flèche traversant les murs et émettant une décharge d’énergie, révélant et blessant tous les ennemis sur son passage.","Fureur du Chasseur","/VideoSpell/Sova/SX.png"),new Signature(0,1,2.5,"Sova s’équipe d’un arc. TIREZ pour envoyer une flèche de reconnaissance qui se fixe sur le premier obstacle touché. La flèche émet des pulsations qui révèlent la position des adversaires s’ils sont dans la ligne de visée de l’émetteur. Les adversaires peuvent détruire la flèche. TIR ALTERNATIF pour ajouter jusqu’à deux rebonds à la flèche avant explosion.",40,"Secondes","Flèche de Reconnaissance","/VideoSpell/Sova/SE.png"),new Capacite(150,2,4,"Sova s’équipe d’un arc. TIREZ pour lancer une flèche qui explose à l’impact et blesse les adversaires dans la zone d’impact. TIR ALTERNATIF pour ajouter jusqu’à deux rebonds à la flèche avant explosion.","Électroflèche","/VideoSpell/Sova/SA.png"),new Capacite(400,1,4,"Sova s’équipe d’un drone. TIREZ pour déployer le drône. Vous en prenez alors le contrôle pendant une courte durée. Vous pouvez tirer sur un adversaire avec le drône pour révéler sa présence à vos alliés.","Drône Rapace","/VideoSpeLl/Sova/SC.png"),new Passif("Null","Null"),"/VideoSpell/Sova/sova.jpg","/VideoSpell/Sova/russie.png","Sova est un chasseur hors pair. Il excelle dans l’art d’initier les combats et de révéler la position de l’équipe ennemie. Ses flèches trouvent toujours l’adversaire, et son drone rapace lui permet d’explorer le terrain sans se mettre en danger. Il est un combattant de choix et un atout très précieux dans l’équipe."),
                new Valorant("Chamber","Sentinels",new ListeAg(),"France",new Ultime(7,"ACTIVEZ pour matérialiser un puissant fusil de sniper personnalisé qui tue l’ennemi en un coup.Tuer un ennemi crée un champ persistant qui ralentit les joueurs qui s’y trouvent.","Tour de Force","/VideoSpell/Chamber/CX.png"),new Signature(0,2,4,"PLACEZ deux balises de téléportation. Quand vous vous trouvez sur le sol à portée d’une balise, RÉACTIVEZ pour vous téléporter rapidement vers l’autre balise. Les balises peuvent être ramassées pour être REDÉPLOYÉES.",0,"Null","Rendez-vous","/VideoSpell/Chamber/CE.png"),new Capacite(100,8,0,"ACTIVEZ pour équiper un gros calibre. Faites un TIR SECONDAIRE avec le pistolet équipé pour utiliser le viseur.","Chasseur de têtes","/VideoSpell/Chamber/CA.png"),new Capacite(150,2,4,"POSEZ un piège qui détecte les ennemis. Quand un ennemi devient visible à portée, le piège déclenche un compte à rebours avant d’ébranler le terrain autour de lui, créant u n champ persistant qui ralentit les joueurs qui s’y trouvent.","Marque déposée","/VideoSpell/Chamber/CC.png"),new Passif("Null","Null"),"/VideoSpell/Chamber/chamber.jpg","/VideoSpell/Chamber/france.png","Chamber est une Sentinelle française, disposant de tout un arsenal d’armes pour aussi bien défendre qu’attaquer une position. Agent complexe, vous devrez faire preuve de précision et d’une bonne dose de skill pour parvenir à le maîtriser."),
                new Valorant("Cypher","Sentinels",new ListeAg(),"Morocco",new Ultime(6,"Cypher révèle instantanément la position de tous les ennemis encore en vie lorsqu’il cible le cadavre d’un adversaire.","Vol Neural","/VideoSpell/Cypher/CX.png"),new Signature(0,1,0,"Cypher s’équipe d’une caméra espion. TIREZ pour placer la caméra sur une surface. ACTIVEZ la capacité pour contrôler la caméra. Pendant que vous la contrôlez, vous pouvez tirer une fléchette émettrice sur un adversaire pour révéler sa position à tous vos alliés. La caméra peut être récupérée puis ré-utilisée après un court laps de temps.",45,"Secondes","Caméra Espionne","/VideoSpell/Cypher/CE.png"),new Capacite(100,2,3,"Cypher s’équipe de pièges. TIREZ pour placer un piège au sol. ACTIVEZ la capacité pour créer une zone bloquant le champ de vision des adversaires.","Cybercage","/VideoSpell/Cypher/CA.png"),new Capacite(200,2,2,"Cypher s’équipe d’un piège à fil. TIREZ pour placer un dispositif créant un fil jusqu’au mur opposé. Le premier adversaire qui traverse le fil est ralenti et révélé à tous vos alliés. Les adversaires peuvent détruire le dispositif. Le rayon est invisible aux yeux des adversaires à moins de se trouver à courte portée.","Fil de détente","/VideoSpell/Cypher/CC.png"),new Passif("Nul","Null"),"/VideoSpell/Cypher/cypher.jpg","/VideoSpell/Cypher/maroc.png","Cypher est un adversaire redoutable. Rien de ce que fait l’équipe ennemie ne lui échappe, et le rôle de Sentinelle lui va à ravir. Pièges, caméra, fil de détection… Il a tout l’arsenal d’un véritable espion, et sera un véritable atout pour révéler la position de l’équipe adverse à ses coéquipiers. Son piège lui permet également de ralentir et rétrécir le champ de vision de ses ennemis. Vous l’aurez compris, Cypher est donc quelqu’un qu’il faut mieux avoir dans son équipe que contre soi."),
                new Valorant("Killjoy","Sentinels",new ListeAg(),"Germany",new Ultime(7,"ÉQUIPEZ-vous d’un dispositif de confinement.TIREZ pour déployer le dispositif au sol. Le dispositif se met à emmagasiner de l’énergie pendant 13 secondes avant d’exploser sur une très large zone. Les ennemis pris dans l’explosion ne subissent pas de dégâts, mais leurs mouvements sont très lourdement impactés pendant 8 secondes.Le dispositif possède 150 PV et peut être détruit.","Confinement","/VideoSpell/KJ/KX.png"),new Signature(0,1,0,"TIREZ pour déployer une tourelle automatique. La tourelle prend pour cible les ennemis dans un large cône d’une amplitude de 180 degrés. Killjoy peut ramasser la tourelle et la redéployer à un autre endroit au cours du round.La tourelle possède 125 PV. Elle fait 8 points de dégâts par tir qui diminuent avec la distance. Elle fait 6 points de dégâts par tir à moins de 20 mètres, puis 4 points de dégâts à partir de 35 mètres.",45,"Secondes","Tourelle","/VideoSpell/KJ/KE.png"),new Capacite(200,1,0,"TIREZ pour déployer le robot au sol. Le robot se rend invisible et attend que des adversaires passent dans le coin. Lorsqu’Alarmbot détecte un ennemi, il devient visible et se dirige vers sa victime avant d’exploser. En plus des dégâts occasionnés, l’adversaire subit également l’altération d’état Vulnérable.MAINTENEZ LA TOUCHE D’ÉQUIPEMENT pour rappeler un robot déployé.","Bot-Alarme","/VideoSpell/KJ/KA.png"),new Capacite(200,2,0,"ÉQUIPEZ-vous d’une grenade à essaim de nanites.TIREZ pour lancer la grenade. À l’atterrissage, la grenade se camoufle. ACTIVEZ la grenade pour déployer un essaim de nanites.","Essaim de Nanites","/VideoSpeLl/KJ/KC.png"),new Passif("Null","Null"),"/VideoSpell/KJ/kj2.jpg","/VideoSpell/KJ/allemagne.png","Killjoy est une Agent haute en couleurs qui fait partie des Sentinelles. Véritable génie originaire d’Allemagne, Killjoy sécurise et défend les positions clés à l’aide de pièges, de tourelles et de mines. Ses inventions punissent tout agresseur trop idiot pour abandonner. Elle contrôle les zones de combat et impose des timings d’attaque ou de défense désavantageux pour ses adversaires. En bref, elle porte très bien son nom."),
                new Valorant("Sage","Sentinels",new ListeAg(),"China",new Ultime(8,"Sage s’équipe de la Résurrection. TIREZ sur le cadavre d’un allié pour le ramener à la vie avec tous ses points de vie après un court temps d’incantation.","Résurrection","/SX.png"),new Signature(0,1,3,"Sage s’équipe d’un orbe de soin. TIREZ sur un allié pour le soigner. TIR ALTERNATIF pour vous soigner vous-même.",45,"Secondes","Orbe de Soin","/SE.png"),new Capacite(200,2,3,"Sage s’équipe d’un orbe de ralentissement. TIREZ pour lancer l’orbe qui explose en touchant le sol, créant une large zone ralentissant les joueurs se tenant dessus.","Orbe de Lenteur","/SA.png"),new Capacite(400,1,15,"Sage s’équipe d’un orbe mural. TIREZ pour créer un mur solide bloquant la vision, les tirs et les mouvements de toutes les unités. TIR ALTERNATIF pour changer l’angle du mur. Le mur se désagrège petit à petit avec le temps ou quand il subit des tirs.","Orbe Barrière","/SC.pn"),new Passif("null","Null"),"VideoSpell/Sage/sage.jpg","/VideoSpell/Sage/chine.jpg","L’Agent Sage est l’une des sentinelles de Valorant. Son kit de compétences est très utile, et lui permet à la fois de réduire le mouvement ou la vision des adversaires, mais également de soigner ou de ressusciter un allié. Elle est très complète et sera assurément un Agent de choix à intégrer à votre équipe.")
            });

        }
        public void chargeCarte() {
            carte.AddRange(new Carte[]
               {
            new Carte("Ascent","/image/Cartes/Ascent/Ascent.jpeg","/image/Cartes/Ascent/inGame.png","Ascent est la quatrième map de Valorant, ajoutée à la sortie du jeu au cours du patch 1.00. Elle présente 2 spots de Spike séparés par de longs couloirs et une place centrale ouverte. Certains accès peuvent être restreint par des rideaux de fer déployés par les défenseurs.La map met en avant la verticalité; en faveur des Défenseurs sur le site A, en faveur des Attaquants sur le site B.Retrouvez une présentation plus complète d’Ascent."),
            new Carte("Bind","/image/Cartes/Bind/Bind.jpg","/image/Cartes/Bind/inGame.png","Bind est une map qui dispose de deux site de Spike. Le premier, en A, est une zone découverte, protégée par de nombreuses caisses en bois et une fenêtre en hauteur, propice à la défense au sniper. Le second, en B, se situe sous un tunnel métallique, difficilement accessible.Sa particularité réside dans ses téléporteurs, qui permettent de traverser la map de long en large et tenter de prendre par surprise son adversaire. Cependant, ils sont très bruyants et il est facile de se faire punir à leur sortie, qu’on soit venu attaquer ou défendre."),
            new Carte("Breeze","/image/Cartes/Breeze/Breeze.png","/image/Cartes/Breeze/InGame.jpeg","Breeze est la sixième map de Valorant, ajouté lors de l’Acte 3 de l’Épisode 2. Il s’agit d’une map aux multiples zones ouvertes, avec de nombreux embranchements dans ses couloirs.Elle possède deux sites de Spike : l’un est légèrement surélevé, l’autre est légèrement en contre-bas. Les deux sites proposent relativement peu d’abris pour les combats, les Agents vont donc devoir utiliser leurs compétences pour se frayer un chemin d’un endroit à un autre.Retrouvez une présentation plus complète de Breeze."),
            new Carte("Fracture","/image/Cartes/Fracture/Fracture.jpeg","/image/Cartes/Fracture/InGame.jpeg","Fracture est la septième map de Valorant, ajouté lors de l’Acte 2 de l’Épisode 3. Il s’agit d’une map originale, où les défenseurs sont cernés par les attaquants. Les points A et B sont particulièrement difficiles à défendre, du fait que les assauts peuvent venir de toute part et que les sites ne proposent ni couverture, ni issue de secours.Cette map fait la part belle aux affrontement à courte et moyenne portée. Les Agents capables de verrouiller une zone ou un accès y sont particulièrement efficaces.Retrouvez une présentation plus complète de Fracture."),
            new Carte("Haven","/image/Cartes/Haven/Haven.jpg","/image/Cartes/Haven/InGame.png","Haven est une map très particulière, puisqu’elle dispose de 3 sites pour la spike : A, B et C.Les défenseurs ont alors de nombreux points stratégiques à couvrir face aux attaquants, et doivent intelligemment répartir les ressources sur l’ensemble du terrain.Disposant de nombreux recoins et de portes d’entrées vers les sites, Haven est une map parfaite pour apprendre à maîtriser ses déplacements et créer des stratégiques innovantes."),
            new Carte("Icebox","/image/Cartes/Icebox/Icebox.jpg","/image/Cartes/Icebox/InGame.jpeg","Icebox est la cinquième map de Valorant, ajoutée au lancement de l’Acte 3 de l’Episode 1. Il s’agit d’une map très ouverte et verticale, où le combat a longue distance y est roi.Elle possède deux sites de Spike aux styles radicalement opposés. Le site A est en intérieur et plutôt claustrophobique tandis que le site B est ouvert et en extérieur.Retrouvez une présentation plus complète d’Icebox."),
            new Carte("Split","/image/Cartes/Split/Split.jpg","/image/Cartes/Split/InGame.png","Split est une grande carte qui possède deux sites de Spike, assez éloignés l’un de l’autre. Elle est certainement l’un des plus difficile à maîtriser pour le moment, puisqu’elle nécessite une bonne connaissance des lieux pour se repéré et piéger ses adversaires.Les défenseurs sont mis à rude épreuve sur Split, puisqu’en cas de mauvais choix ou de défaite sur l’un des spots, le reste de l’équipe doit effectuer un long trajet pour espérer freiner l’avancée des attaquants. Heureusement pour eux, il reste difficile de percer les défenses dans un premier temps, puisque les espaces de la map sont propices à retenir les assauts.")
               });
        }
        public Valorant ValorantSelectionne(string nom)
        {
            return GetValorant(nom);
        }
    }
}
